<template>
  <div v-if="$fetchState.error">Error while fetching project</div>
  <BigProjectCard v-else :loading="$fetchState.pending" :data="project" />
</template>

<script>
import BigProjectCard from '@/components/Site/Cards/BigProjectCard'

export default {
  components: {
    BigProjectCard
  },
  data() {
    return {
      project: {}
    }
  },
  fetchDelay: 250,
  async fetch() {
    // Long title = 145
    const spotId = await this.$axios.get('/api/spot/1')
    const projectId = spotId.data.response

    if (!projectId) {
      throw new Error('No project ID was returned')
    }

    const { data, error } = await this.$supabase
      .from('projects')
      .select('*')
      .eq('id', projectId)
      .single()

    if (error) {
      throw new Error('No project found')
    }

    if (data) {
      const BUCKET = 'public'
      const bannerName = data.banner_name
      const bannerDefault =
        'data:image/png;base64,'

      if (bannerName) {
        const bannerResp = await this.$supabase.storage
          .from(BUCKET)
          .getPublicUrl(bannerName)

        if (bannerResp.data) {
          data.bannerURL = bannerResp.data.publicURL
        } else {
          data.bannerURL = bannerDefault
        }
      } else {
        data.bannerURL = bannerDefault
      }

      this.project = data
    }
  }
}
</script>

