<template>
  <NuxtLink :to="`/@${data.slug}`">
    <div class="box-border relative justify-between flex flex-col items-stretch self-stretch flex-auto flex-grow flex-shrink-0 max-w-full p-4 md:px-8 md:pt-8 md:pb-10 m-0 overflow-hidden leading-6 text-black bg-gray-300  border-black border-solid rounded-xl outline-none cursor-pointer project-image min-h-[280px] lg:min-h-[280px] bg-glancing" :style="{'background-image': `url('${image}')`}">

      <span class="absolute z-10 text-xs text-gray-300 top-3 right-4">âœ¨ Sponsored</span>

      <!-- button -->
      <div class="z-10 flex justify-between">

      </div>

      <!-- Avatar -->
      <div class="z-10 flex items-center justify-between">
        <div class="z-10 flex flex-col items-start md:items-center md:flex-row">
          <ProjectAvatar :verified="data.verified" verified-tooltip :name="data.name" :size="64" :size-badge="26" :avatar-color="data.avatar_color" :avatar-name="data.avatar_name" />
          <div class="flex flex-col items-start md:ml-4">
            <h2 class="box-border relative z-10 text-xl font-black text-white lg:text-2xl">
              {{ data.name }}
            </h2>

            <ProjectTotalCount :id="data.id" :animated="false" type="empty" class="text-white" />
          </div>
        </div>
        <div v-if="eventTime && eventName" class="text-right">
          <client-only>
            <div class="mb-1 text-xs text-right text-gray-100">{{ eventName }} in</div>
            <Countdown :time="eventTime" class="flex flex-row text-white">
              <template slot-scope="props">
                <div class="box-border w-6 h-6 p-0 m-0 leading-6 text-center text-gray-900 bg-white rounded cursor-pointer">
                  {{ props.days }}
                </div>
                <span>:</span>
                <div class="box-border w-6 h-6 p-0 m-0 leading-6 text-center text-gray-900 bg-white rounded cursor-pointer">
                  {{ props.hours }}
                </div>
                <span>:</span>
                <div class="box-border w-6 h-6 p-0 m-0 leading-6 text-center text-gray-900 bg-white rounded cursor-pointer">
                  {{ props.minutes }}
                </div>
                <span>:</span>
                <div class="box-border w-6 h-6 p-0 m-0 leading-6 text-center text-gray-900 bg-white rounded cursor-pointer">
                  {{ props.seconds }}
                </div>
              </template>
            </Countdown>
          </client-only>
        </div>
      </div>
    </div>
  </NuxtLink>
</template>

<script>
import Countdown from '@/components/Site/Countdown'
import ProjectAvatar from '@/components/ProjectAvatar'
import ProjectTotalCount from '@/components/Project/ProjectTotalCount'

export default {
  components: {
    Countdown,
    ProjectAvatar,
    ProjectTotalCount
  },
  props: {
    data: {
      type: Object,
      default: () => {},
      required: true
    },
    loaderSize: {
      type: Number,
      default: 300,
      required: false
    },
    eventTime: {
      type: Number,
      default: 0,
      required: false
    },
    eventName: {
      type: String,
      default: '',
      required: false
    }
  },
  data() {
    return {
      image: null
    }
  },
  async fetch() {
    let bannerUrl
    const bannerName = this.data.banner_name
    const BUCKET = 'public'
    const bannerDefault =
      'data:image/png;base64,'

    if (bannerName) {
      const bannerResp = await this.$supabase.storage
        .from(BUCKET)
        .getPublicUrl(bannerName)

      if (bannerResp.data) {
        bannerUrl = bannerResp.data.publicURL
      } else {
        bannerUrl = bannerDefault
      }
    } else {
      bannerUrl = bannerDefault
    }

    this.image = bannerUrl
  }
}
</script>

