  content: '';
  position: absolute;
  inset: 0px;
  z-index: 1;
  transform: translate3d(0px, 0px, 0px);
  border-radius: 12px;
  background: linear-gradient(
    180deg,
    rgb(205 22 22 / 0%) 1%,
    rgb(0 0 0 / 66%) 93%
  );
}

.bg-glancing {
  object-fit: cover;
  background-size: cover;
  background-position: 50;
  animation: animatedBackgroundY 72s linear infinite;
}

